mapper.update:
update chat
set readed = 1
where userid = #{arg0}
  and touserid = #{arg1}

mapper.select:
select chat.userid, chat.touserid, chat.content, chat.createtime
from chat
where (userid = #{arg0}
    and touserid = #{arg1})
   or (userid = #{arg1}
    and touserid = #{arg0})
order by createtime desc
    limit #{arg2}, #{arg3}

select chat.userid, chat.touserid, chat.content, chat.createtime
from chat
where (userid = #{arg0}
    and touserid = #{arg1})
   or (userid = #{arg1}
    and touserid = #{arg0})
order by createtime desc
    limit #{arg2}, #{arg3}
